-- görünüm yapısı dökülüyor yonapp.kategoribazliislemler
DROP VIEW IF EXISTS `kategoribazliislemler`;
-- VIEW bağımlılık sorunlarını çözmek için geçici tablolar oluşturuluyor
CREATE TABLE `kategoribazliislemler` (
	`kisi_id` INT(11) NULL,
	`islem_adi` VARCHAR(1) NULL COLLATE 'utf8mb4_general_ci',
	`tarih` DATETIME NULL,
	`tutar` DECIMAL(12,2) NULL,
	`aciklama` MEDIUMTEXT NULL COLLATE 'utf8mb4_general_ci',
	`islem_turu` VARCHAR(1) NULL COLLATE 'utf8mb4_general_ci',
	`bakiye` DECIMAL(34,2) NULL
) ENGINE=MyISAM;

-- görünüm yapısı dökülüyor yonapp.kategoribazliislemozet
DROP VIEW IF EXISTS `kategoribazliislemozet`;
-- VIEW bağımlılık sorunlarını çözmek için geçici tablolar oluşturuluyor
CREATE TABLE `kategoribazliislemozet` (
	`kisi_id` INT(11) NULL,
	`kategori_adi` VARCHAR(1) NULL COLLATE 'utf8mb4_general_ci',
	`bakiye` DECIMAL(34,2) NULL,
	`kayit_sayisi` BIGINT(21) NOT NULL
) ENGINE=MyISAM;

-- Geçici tablolar temizlenerek final VIEW oluşturuluyor
DROP TABLE IF EXISTS `kategoribazliislemler`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `kategoribazliislemler` AS SELECT 
    kisi_id,
    islem_adi,
    tarih,
    tutar,
    aciklama,
    islem_turu,
    SUM(CASE 
        WHEN islem_turu = 'borç' THEN -tutar 
        ELSE tutar 
    END) OVER(PARTITION BY kisi_id ORDER BY tarih ROWS UNBOUNDED PRECEDING) AS bakiye
FROM (
    SELECT 
        kisi_id,
        borc_adi AS islem_adi,
        olusturma_tarihi AS tarih,
        tutar,
        aciklama,
        'borç' AS islem_turu
    FROM borclandirma_detayi 
    
    UNION ALL 
    
    SELECT 
        kisi_id,
        tahsilat_tipi AS islem_adi,
        islem_tarihi AS tarih,
        tutar,
        aciklama,
        'tahsilat' AS islem_turu
    FROM tahsilatlar
) AS tum_islemler
ORDER BY kisi_id, tarih desc ;

-- Geçici tablolar temizlenerek final VIEW oluşturuluyor
DROP TABLE IF EXISTS `kategoribazliislemozet`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `kategoribazliislemozet` AS SELECT 
	 kisi_id,
    islem_adi AS kategori_adi,
    SUM(CASE 
        WHEN islem_turu = 'borç' THEN -tutar 
        WHEN islem_turu = 'tahsilat' THEN tutar 
        ELSE 0 
    END) AS bakiye,
    COUNT(*) AS kayit_sayisi
FROM kategoribazliislemler
GROUP BY kisi_id , islem_adi ;

